     def Save(self):
   
        # check if any fields are empty
         for val in self.data.values():
            if val.get() == "":
                messagebox.showinfo("Error", "Please Fill in all the Fields")
                return

        # Save the room
         result = db_controller.add_class(  
            *[self.data[label].get() for label in ("subject", "teacher", "class_hour", "class_code", "lesson","class_availability")]
        )

         if result:
            messagebox.showinfo("Success", "Class Details Registered Successfully")
            self.parent.navigate("view")
            self.parent.windows.get("view").handle_refresh()
            # clear all fields
            for label in self.data.keys():
                self.data[label].set('')
         else:
            messagebox.showerror(
                "Error", "Unable to Activity. Please make sure the data is validated"
            )
       
     def clear_entries(self):
        # Iterate over all entry fields and set their values to an empty string
        for var in self.data.values():
            var.set("")
            


